sys_script.script:
(function executeRule(current, previous) {
    if (current.table_new == 'incident') {
        var attRec = new GlideRecord('sys_attachment');
        attRec.addQuery('table_sys_id', current.table_sys_id);
        attRec.query();
        var getCount = 0; // Initialize outside of the loop
        while (attRec.next()) {
            getCount++;
            if (getCount > 2) {
                gs.addErrorMessage('Maximum number of attachments allowed is 3');
                current.setAbort(true); // Corrected setAbortAction to setAbort
            }
        }
    }
})();
